import React, { useState, useEffect } from 'react';
import { BrowserRouter } from 'react-router-dom';
import Navigation from './Navigation';
import Signup from './Signup';
import Login from './Login';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [role, setRole] = useState('');

    useEffect(() => {
        const loggedIn = localStorage.getItem('isLoggedIn') === 'true';
        const storedUsername = localStorage.getItem('username') || '';
        const storedRole = localStorage.getItem('role') || '';
        setIsLoggedIn(loggedIn);
        setUsername(storedUsername);
        setRole(storedRole);
    }, []);

    const handleLoginSuccess = (token, user) => {
        setIsLoggedIn(true);
        setUsername(user.username); // 更新用户名
        setRole(user.role); // 更新角色
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('username', user.username); // 存储用户名
        localStorage.setItem('role', user.role); // 存储角色
        localStorage.setItem('token', token); // 存储 token
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        setRole('');
        localStorage.removeItem('isLoggedIn');
        localStorage.removeItem('username');
        localStorage.removeItem('role');
        localStorage.removeItem('token'); // 移除 token
    };

    return (
        <div className="container">

            {isLoggedIn && <Navigation onLogin={handleLoginSuccess} onLogout={handleLogout} role={role}/>}
            {isLoggedIn && <h1>Welcome, {username} ({role})!</h1>}
            {!isLoggedIn && (
                <div>
                    <h1>Login</h1>
                    <Login onLoginSuccess={handleLoginSuccess} />
                </div>
            )}
        </div>
    );
}

export default App;
