import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './Navigation.css';
import { CartContext } from './T_CarContext';

const Navigation = ({ onLogout, role,username, isLoggedIn }) => {
    const [menuOpen, setMenuOpen] = useState(false);
    const menuRef = useRef(null);

    const toggleMenu = () => {
        setMenuOpen((prev) => !prev);
    };

    const handleClickOutside = (event) => {
        if (menuRef.current && !menuRef.current.contains(event.target)) {
            setMenuOpen(false);
        }
    };

    useEffect(() => {
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    const [cartItems, setCartItems] = useState([]);

   
        const [openSubMenu, setOpenSubMenu] = useState(null);
      
        const toggleSubMenu = (index) => {
          setOpenSubMenu(openSubMenu === index ? null : index);
        };

    return (
        <CartContext.Provider value={{ cartItems, setCartItems }}>
            <div className="navigation-container">
                <img 
                    src={process.env.PUBLIC_URL + 'Images/icon-menu.svg'} 
                    alt="Menu" 
                    className="menu-icon" 
                    onClick={toggleMenu} 
                />
                <div className="logo-container">
                    <img 
                        src={process.env.PUBLIC_URL + 'Images/logo-buyinsy.png'} 
                        alt="Logo" 
                        className="logo" 
                    />
                </div>
                {menuOpen && (
                    <div className="dropdown-menu" ref={menuRef}>
                        {isLoggedIn && <h1>Welcome, {username}!</h1>}
                        <Link to="#" onClick={toggleMenu}>全部商店</Link>
                        
                        <Link to="/Recipt" onClick={toggleMenu}>Recipt</Link>

                        <h2 onClick={() => toggleSubMenu(1)} style={{ cursor: 'pointer' }}>
          User Management (用戶管理)
        </h2>
        {openSubMenu === 1 && (
          <div style={{ paddingLeft: '20px' }}>
            <ul>
              <li><Link to="/statistic" onClick={toggleMenu}>銷量情報</Link></li>
              <li>Edit Shop User (編輯店鋪用戶)</li>
              <li>Delete Shop User (刪除店鋪用戶)</li>
            </ul>
          </div>
        )}
                        
                        {role === 'admin' && (
                            <>
                                <Link to="/Signup" onClick={toggleMenu}>SignUp</Link>   
                                <Link to="/OrderPage" onClick={toggleMenu}>OrderPage</Link>
                                <Link to="/productslist" onClick={toggleMenu}>Products List</Link>
                                <Link to="/coursetest" onClick={toggleMenu}>Course Test</Link>
                            </>
                        )}
                        {!isLoggedIn ? (
                            <Link to="/login" onClick={toggleMenu}>Login</Link> // Show Login if not logged in
                        ) : (
                            <Link to="/" onClick={() => { onLogout(); toggleMenu(); }}>Logout</Link> // Show Logout if logged in
                        )}
                    </div>
                )}
            </div>
        </CartContext.Provider>
    );
};

export default Navigation;
