import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useTranslation } from 'react-i18next'; // Import useTranslation
import { useNavigate } from 'react-router-dom';

const Signup = () => {
    const { t } = useTranslation(); // Use the translation hook
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [content, setContent] = useState('');
    const [generatedPassword, setGeneratedPassword] = useState('');
    const [role, setRole] = useState('buyer'); // Default role
    const navigate = useNavigate();

    useEffect(() => {
        generatePassword(); // Automatically generate password on component mount
    }, []);
    const generatePassword = () => {
        const length = 8;
        const charset = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
        let password = '';
        for (let i = 0; i < length; i++) {
            const randomIndex = Math.floor(Math.random() * charset.length);
            password += charset[randomIndex];
        }
        setGeneratedPassword(password); // Update the generated password state     
        setContent(`Your Password is ${password}`); // Set initial content with password
    };  

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/signup', { username, password, role });
            alert(t('User created!')); // Use translation for alerts
        } catch (error) {
            alert(t('Signup failed: ') + error.response.data);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                placeholder={t('Username')}
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <input
                type="password"
                placeholder={t('Password')}
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
             <input
                type="email"
                placeholder="Enter target email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
            />
            <textarea
                placeholder="Enter email content"
                value={content}
                onChange={(e) => setContent(e.target.value)}
            />
            <input
                type="text"
                placeholder="Generated Password"
                value={generatedPassword}
                readOnly // Make this input read-only
            />
            <select value={role} onChange={(e) => setRole(e.target.value)}>
                <option value="admin">{t('Admin')}</option>
                <option value="shopuser">{t('Shop User')}</option>
                <option value="buyer">{t('Buyer')}</option>
            </select>
            <button type="submit">{t('Sign Up')}</button>
        </form>
    );
};

export default Signup;
