import React, { useState, useEffect, useRef } from 'react';
import { Link, Routes, Route } from 'react-router-dom';
import './Navigation.css';
import Statistics from './Statistics';
import BuyEaseForm from './BuyEaseForm';
import Recipt from './Recipt';
import OrderPage from './OrderPage';
import ProductsList from './ProductsList';
import CourseTest from './CourseTest';
import { CartContext } from './T_CarContext';

const Navigation = ({ onLogout, role, isLoggedIn }) => {
    const [menuOpen, setMenuOpen] = useState(false);
    const menuRef = useRef(null);

    const toggleMenu = () => {
        setMenuOpen((prev) => !prev);
    };

    const handleClickOutside = (event) => {
        if (menuRef.current && !menuRef.current.contains(event.target)) {
            setMenuOpen(false);
        }
    };

    useEffect(() => {
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    const [cartItems, setCartItems] = useState([]);

    return (
        <CartContext.Provider value={{ cartItems, setCartItems }}>
            <div className="navigation-container">
                <img 
                    src={process.env.PUBLIC_URL + 'Images/icon-menu.svg'} 
                    alt="Menu" 
                    className="menu-icon" 
                    onClick={toggleMenu} 
                />
                <div className="logo-container">
                    <img 
                        src={process.env.PUBLIC_URL + 'Images/logo-buyinsy.png'} 
                        alt="Logo" 
                        className="logo" 
                    />
                </div>
                {menuOpen && (
                    <div className="dropdown-menu" ref={menuRef}>
                        <Link to="#" onClick={toggleMenu}>全部商店</Link>
                        <Link to="/statistic" onClick={toggleMenu}>銷量情報</Link>
                        <Link to="/Recipt" onClick={toggleMenu}>Recipt</Link>
                        {role === 'admins' && (
                            <>
                                <Link to="/OrderPage" onClick={toggleMenu}>OrderPage</Link>
                                <Link to="/productslist" onClick={toggleMenu}>Products List</Link>
                                <Link to="/coursetest" onClick={toggleMenu}>Course Test</Link>
                            </>
                        )}
                        {!isLoggedIn ? (
                            <Link to="/login" onClick={toggleMenu}>Login</Link> // Login link for non-logged users
                        ) : (
                            <Link to="/" onClick={onLogout}>Logout</Link>
                        )}
                    </div>
                )}
            </div>
            <Routes>
                <Route path="/statistic" element={<Statistics />} />
                <Route path="/BuyEaseForm" element={<BuyEaseForm />} />
                <Route path="/Recipt" element={<Recipt />} />
                <Route path="/OrderPage" element={<OrderPage isLoggedIn={isLoggedIn} role={role} />} />
                <Route path="/productslist" element={<ProductsList />} />
                <Route path="/coursetest" element={<CourseTest />} />
            </Routes>
        </CartContext.Provider>
    );
};

export default Navigation;
