import React from 'react';

const generateAnalogousColors = (baseColor, count) => {
    const colors = [];
    const baseRGB = hexToRgb(baseColor);
    
    for (let i = 0; i < count; i++) {
        const offset = (i - Math.floor(count / 2)) * 20; // 調整色彩的偏移量
        const newColor = rgbToHex(baseRGB[0] + offset, baseRGB[1], baseRGB[2]);
        colors.push(newColor);
    }
    
    return colors;
};

const hexToRgb = (hex) => {
    const bigint = parseInt(hex.slice(1), 16);
    const r = (bigint >> 16) & 255;
    const g = (bigint >> 8) & 255;
    const b = bigint & 255;
    return [r, g, b];
};

const rgbToHex = (r, g, b) => {
    return `#${((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1).toUpperCase()}`;
};

const ColorPalette = ({ count }) => {
    const baseColor = '#D94A3D';
    const colors = generateAnalogousColors(baseColor, count);

    return (
        <div style={{ display: 'flex', gap: '10px' }}>
            {colors.map((color, index) => (
                <div key={index} style={{ backgroundColor: color, width: '100px', height: '100px' }}>
                    {color}
                </div>
            ))}
        </div>
    );
};

// 使用範例
const App = () => {
    return (
        <div>
            <h1>類比色彩調和</h1>
            <ColorPalette count={5} />
        </div>
    );
};

export default App;
