import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import { useTranslation } from 'react-i18next'; // Import useTranslation

const SignupAndSendEmail = () => {
    const { t } = useTranslation(); // Use the translation hook
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [role, setRole] = useState('buyer'); // Default role
    const [email, setEmail] = useState('');
    const [content, setContent] = useState('');
    const [generatedPassword, setGeneratedPassword] = useState('');

    const navigate = useNavigate();

    useEffect(() => {
        generatePassword(); // Automatically generate password on component mount
    }, []);

    const handleSignup = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/signup', { username, password, role });
            alert(t('User created!')); // Use translation for alerts
        } catch (error) {
            alert(t('Signup failed: ') + error.response.data);
        }
    };

    const handleSendEmail = async () => {
        try {
            const response = await axios.post('http://localhost:5000/send-email', {
                email,
                content,
                header: 'testing header',
            });
            alert(response.data); // Show success message
            navigate('/'); // Redirect to the root path
        } catch (error) {
            alert('Error sending email: ' + error.message);
        }
    };

    const generatePassword = () => {
        const length = 8;
        const charset = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
        let password = '';
        for (let i = 0; i < length; i++) {
            const randomIndex = Math.floor(Math.random() * charset.length);
            password += charset[randomIndex];
        }
        setGeneratedPassword(password); // Update the generated password state     
        setContent(`Your Password is ${password}`); // Set initial content with password
    };

    return (
        <div>
            <h1>{t('Signup and Send Email')}</h1>
            <form onSubmit={handleSignup}>
                <input
                    type="text"
                    placeholder={t('Username')}
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    required
                />
                <input
                    type="password"
                    placeholder={t('Password')}
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
                <select value={role} onChange={(e) => setRole(e.target.value)}>
                    <option value="admin">{t('Admin')}</option>
                    <option value="shopuser">{t('Shop User')}</option>
                    <option value="buyer">{t('Buyer')}</option>
                </select>
                <button type="submit">{t('Sign Up')}</button>
            </form>

            <hr />

            <h2>{t('Send Test Email')}</h2>
            <input
                type="email"
                placeholder={t('Enter target email')}
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
            />
            <textarea
                placeholder={t('Email content')}
                value={content}
                onChange={(e) => setContent(e.target.value)}
            />
            <input
                type="text"
                placeholder={t('Generated Password')}
                value={generatedPassword}
                readOnly // Make this input read-only
            />
            <button onClick={handleSendEmail}>{t('Send Email')}</button>
        </div>
    );
};

export default SignupAndSendEmail;
