import React, { useState } from 'react';
import './Navigation.css';
import './BuyEaseForm.css';


const Navigation = () => {
    const [menuOpen, setMenuOpen] = useState(false);

    const toggleMenu = () => {
        setMenuOpen(!menuOpen);
    };
    return (
        <div>
             <img src={process.env.PUBLIC_URL+'Images/icon-menu.svg'} alt="Menu" className="menu-icon" />
            <h1>
                <img src={process.env.PUBLIC_URL+'Images/logo-buyinsy.png'} alt="Logo" className="logo" />
               
                {menuOpen && <div className="dropdown-menu"> {/* Add your menu items here */} </div>}
            </h1>
        </div>
        );
};

export default Navigation;
