import React, { useState } from 'react';
import axios from 'axios';
import './Login.css';

const Login = ({ onLoginSuccess }) => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/login', { username, password });
            console.log('Response:', response);
            // 确保 response.data 存在
            if (response && response.data) {
                const { token, user } = response.data; // 获取 token 和 user 对象
                const { username, role } = user; // 从 user 对象中提取 username 和 role
                onLoginSuccess(token, { username, role }); // 传递 token 和用户信息
            } else {
                alert('Unexpected response format');
            }
        } catch (error) {
            // 处理错误时，确保访问 error.response
            if (error.response) {
                alert('Login failed: ' + error.response.data);
            } else {
                alert('Login failed: ' + error.message);
            }
        }
    };

    return (
        <form style={{marginTop: '-20px' }} id="login_form" onSubmit={handleSubmit}>
            <h1>Login</h1>
            <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
            />
            <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
            />
            <button type="submit">Login</button>
        </form>
    );
};

export default Login;
