import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navigation from './Navigation';
import Signup from './Signup';
import Login from './Login';
import Statistics from './Statistics'; // Ensure these are imported
import BuyEaseForm from './BuyEaseForm';
import Recipt from './Recipt';
import OrderPage from './OrderPage';
import ProductsList from './ProductsList';
import CourseTest from './CourseTest';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState('');
    const [role, setRole] = useState('');

    useEffect(() => {
        const loggedIn = localStorage.getItem('isLoggedIn') === 'true';
        const storedUsername = localStorage.getItem('username') || '';
        const storedRole = localStorage.getItem('role') || '';
        setIsLoggedIn(loggedIn);
        setUsername(storedUsername);
        setRole(storedRole);
    }, []);

    const handleLoginSuccess = (token, user) => {
        setIsLoggedIn(true);
        setUsername(user.username);
        setRole(user.role);
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('username', user.username);
        localStorage.setItem('role', user.role);
        localStorage.setItem('token', token);
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername('');
        setRole('');
        localStorage.removeItem('isLoggedIn');
        localStorage.removeItem('username');
        localStorage.removeItem('role');
        localStorage.removeItem('token');
    };

    return (
        <Router>
            <div className="container">
                {isLoggedIn && <Navigation onLogout={handleLogout} role={role} isLoggedIn={isLoggedIn} />}
                {isLoggedIn && <h1>Welcome, {username} ({role})!</h1>}
                {!isLoggedIn && (
                    <div>
                        <h1>Login</h1>
                        <Login onLoginSuccess={handleLoginSuccess} />
                    </div>
                )}
                <Routes>
                    <Route path="/signup" element={<Signup />} />
                    <Route path="/login" element={<Login onLoginSuccess={handleLoginSuccess} />} />
                    <Route path="/statistic" element={<Statistics />} />
                    <Route path="/BuyEaseForm" element={<BuyEaseForm />} />
                    <Route path="/Recipt" element={<Recipt />} />
                    <Route path="/OrderPage" element={<OrderPage isLoggedIn={isLoggedIn} role={role} />} />
                    <Route path="/productslist" element={<ProductsList />} />
                    <Route path="/coursetest" element={<CourseTest />} />
                </Routes>
            </div>
        </Router>
    );
}

export default App;
