import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Navigation from './Navigation';
import Signup from './Signup';
import Login from './Login';
import Statistics from './Statistics';
import BuyEaseForm from './BuyEaseForm';
import Recipt from './Recipt';
import OrderPage from './OrderPage';
import ProductsList from './ProductsList';
import CourseTest from './coursetest';
import { CartContext } from './T_CarContext';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [userRole, setUserRole] = useState(null);

    const handleLoginSuccess = (role) => {
        setIsLoggedIn(true);
        setUserRole(role);
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('userRole', role);
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUserRole(null);
        localStorage.removeItem('isLoggedIn');
        localStorage.removeItem('userRole');
    };

    return (
        <BrowserRouter>
            <CartContext.Provider value={{ cartItems: [], setCartItems: () => {} }}>
                <Navigation onLogout={handleLogout} userRole={userRole} />
                <Routes>
                    <Route path="/statistic" element={<Statistics />} />
                    <Route path="/BuyEaseForm" element={<BuyEaseForm />} />
                    <Route path="/Recipt" element={<Recipt />} />
                    <Route path="/OrderPage" element={<OrderPage />} />
                    <Route path="/productslist" element={<ProductsList />} />
                    <Route path="/coursetest" element={<CourseTest />} />
                    {/* 其他路由 */}
                </Routes>
            </CartContext.Provider>
        </BrowserRouter>
    );
}

export default App;
