// src/OrderPage.js
import React, { useState } from 'react';
import SwipeableViews from 'react-swipeable-views';
import styles from './OrderPage.module.css'; // Import the CSS Module

const OrderPage = () => {
    const [activeIndex, setActiveIndex] = useState(0); // Active index for swipeable views

    const items = [
        { month: '4月', orders: [/* 4月的訂單數據 */] },
        { month: '5月', orders: [/* 5月的訂單數據 */] },
        { month: '6月', orders: [/* 6月的訂單數據 */] },
    ];

    const handleChangeIndex = (index) => {
        setActiveIndex(index);
    };

    return (
        <div className={styles.orderPage}>
            <h1>Buyinsy</h1>
            <div className={styles.tabs}>
                {items.map((item, index) => (
                    <button
                        key={index}
                        className={`${styles.tab} ${activeIndex === index ? styles.active : ''}`}
                        onClick={() => handleChangeIndex(index)}
                    >
                        {item.month}訂單
                    </button>
                ))}
            </div>
            <SwipeableViews>
    <div style={Object.assign({}, styles.slide, styles.slide1)}>
      slide n°1
    </div>
    <div style={Object.assign({}, styles.slide, styles.slide2)}>
      slide n°2
    </div>
    <div style={Object.assign({}, styles.slide, styles.slide3)}>
      slide n°3
    </div>
  </SwipeableViews>
            <div className={styles.total}>
                <button className={styles.checkoutButton}>下單</button>
            </div>
        </div>
    );
};

export default OrderPage;
