import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Navigation from './Navigation';
import Login from './Login';
// 其他导入...

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [userRole, setUserRole] = useState(null);
    const [username, setUsername] = useState(''); // 新增用户名状态

    const handleLoginSuccess = (role, user) => {
        setIsLoggedIn(true);
        setUserRole(role);
        setUsername(user); // 更新用户名
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('userRole', role);
        localStorage.setItem('username', user); // 存储用户名
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUserRole(null);
        setUsername(''); // 清空用户名
        localStorage.removeItem('isLoggedIn');
        localStorage.removeItem('userRole');
        localStorage.removeItem('username'); // 移除用户名
    };

    return (
        <BrowserRouter>
            <Navigation onLogout={handleLogout} userRole={userRole} />
            {isLoggedIn && <h1>Welcome, {username}!</h1>} {/* 显示欢迎消息 */}
            <Routes>
                <Route path="/statistic" element={<Statistics />} />
                {/* 其他路由 */}
            </Routes>
        </BrowserRouter>
    );
}

export default App;
