import React, { useState, useEffect, useRef } from 'react';
import { Link } from 'react-router-dom';
import './Navigation.css';
import { CartContext } from './T_CarContext';
import { t } from 'i18next';

const Navigation = ({ onLogout, role,username, isLoggedIn }) => {
    const [menuOpen, setMenuOpen] = useState(false);
    const menuRef = useRef(null);

    const toggleMenu = () => {
        setMenuOpen((prev) => !prev);
    };

    const handleClickOutside = (event) => {
        if (menuRef.current && !menuRef.current.contains(event.target)) {
            setMenuOpen(false);
        }
    };

    useEffect(() => {
        document.addEventListener('mousedown', handleClickOutside);
        return () => {
            document.removeEventListener('mousedown', handleClickOutside);
        };
    }, []);

    const [cartItems, setCartItems] = useState([]);

   
        const [openSubMenu, setOpenSubMenu] = useState(null);
      
        const toggleSubMenu = (index) => {
          setOpenSubMenu(openSubMenu === index ? null : index);
        };

    return (
        <CartContext.Provider value={{ cartItems, setCartItems }}>
            <div className="navigation-container">
                <img 
                    src={process.env.PUBLIC_URL + 'Images/icon-menu.svg'} 
                    alt="Menu" 
                    className="menu-icon" 
                    onClick={toggleMenu} 
                />
                <div className="logo-container">
                    <img 
                        src={process.env.PUBLIC_URL + 'Images/logo-buyinsy.png'} 
                        alt="Logo" 
                        className="logo" 
                    />
                </div>
                {menuOpen && (
                    <div className="dropdown-menu" ref={menuRef}>
                        {isLoggedIn && <h1>{t('Welcome')}, {username}!</h1>}
                        <Link to="#" onClick={toggleMenu}>全部商店</Link>
                        
                        <Link to="/Recipt" onClick={toggleMenu}>Recipt</Link>
                        <Link to="/statistic" onClick={toggleMenu}>銷量情報</Link>


      {/* Guest Menu */}
      <div>
        <h4 onClick={() => toggleSubMenu(20)} style={{ cursor: 'pointer' }}>
          Guest Menu (未登入用戶菜單目錄)
        </h4>
        {openSubMenu === 20 && (
          <div style={{ paddingLeft: '20px' }}>
            <h5 onClick={() => toggleSubMenu(21)} style={{ cursor: 'pointer' }}>
              Dashboard (儀表板)
            </h5>
            {openSubMenu === 21 && (
              <ul>
                <li>Browse Shops (瀏覽店鋪)</li>
                <li>View by Category (按類別查看)</li>
                <li>Search Shops (搜索店鋪)</li>
              </ul>
            )}
            <h5 onClick={() => toggleSubMenu(22)} style={{ cursor: 'pointer' }}>
              Register Account (註冊帳號)
            </h5>
            {openSubMenu === 22 && (
              <ul>
                <li>Sign Up as Buyer (註冊為買家)</li>
                <li>Contact Us (聯絡我們)</li>
                <li>FAQ (常見問題)</li>
                <li>Login (登入)</li>
              </ul>
            )}
          </div>
        )}
      </div>
          {/* Admin Menu */}                
        {role === 'admin' && (
                            <>                          
                <div>
                    <h4 onClick={() => toggleSubMenu(1)} style={{ cursor: 'pointer' }}>
                    {t('SignUp')}12Admin Menu (管理員菜單目錄)
                    </h4>
                    {openSubMenu === 1 && (
                    <div style={{ paddingLeft: '20px' }}>
                        <h5 onClick={() => toggleSubMenu(2)} style={{ cursor: 'pointer' }}>
                        {t('SignUp')}User Management (用戶管理)
                        </h5>
                        {openSubMenu === 2 && (
                        <ul>
                            <li>{t('SignUp')}Create Shop User (創建店鋪用戶)</li>
                            <li>{t('SignUp')}Edit Shop User (編輯店鋪用戶)</li>
                            <li>{t('SignUp')}Delete Shop User (刪除店鋪用戶)</li>
                        </ul>
                        )}
                        <h5 onClick={() => toggleSubMenu(3)} style={{ cursor: 'pointer' }}>
                        {t('SignUp')}Shop Management (店鋪管理)
                        </h5>
                        {openSubMenu === 3 && (
                        <ul>
                            <li>{t('SignUp')}View All Shops (查看所有店鋪)</li>
                            <li>{t('SignUp')}Edit Shop Information (編輯店鋪資訊)</li>
                            <li>{t('SignUp')}Delete Shop (刪除店鋪)</li>
                        </ul>
                        )}
                        <h5 onClick={() => toggleSubMenu(4)} style={{ cursor: 'pointer' }}>
                        {t('SignUp')}Subscription Management (訂閱管理)
                        </h5>
                        {openSubMenu === 4 && (
                        <ul>
                            <li>{t('SignUp')}View All Subscriptions (查看所有訂閱)</li>
                            <li>{t('SignUp')}Verify Subscription (核實訂閱)</li>
                        </ul>
                        )}
                        <h5 onClick={() => toggleSubMenu(5)} style={{ cursor: 'pointer' }}>
                        {t('SignUp')}Promotions Management (活動管理)
                        </h5>
                        {openSubMenu === 5 && (
                        <ul>
                            <li>{t('SignUp')}View All Promotions (查看所有活動)</li>
                            <li>{t('SignUp')}Create Promotion (創建活動)</li>
                            <li>{t('SignUp')}Edit Promotion (編輯活動)</li>
                            <li>{t('SignUp')}Delete Promotion (刪除活動)</li>
                        </ul>
                        )}
                        <h5 onClick={() => toggleSubMenu(6)} style={{ cursor: 'pointer' }}>
                        {t('SignUp')}Coupons Management (禮卷管理)
                        </h5>
                        {openSubMenu === 6 && (
                        <ul>
                            <li>{t('SignUp')}View All Coupons (查看所有禮卷)</li>
                            <li>{t('SignUp')}Create Coupon (創建禮卷)</li>
                            <li>{t('SignUp')}Edit Coupon (編輯禮卷)</li>
                            <li>{t('SignUp')}Delete Coupon (刪除禮卷)</li>
                        </ul>
                        )}
                        <h5 onClick={() => toggleSubMenu(7)} style={{ cursor: 'pointer' }}>
                        {t('SignUp')}Reports & Analytics (報告與分析)
                        </h5>
                        {openSubMenu === 7 && (
                        <ul>
                            <li>{t('SignUp')}Sales Reports (營業報告)</li>
                            <li>{t('SignUp')}User Activity Reports (用戶活動報告)</li>
                        </ul>
                        )}
                        <h5 onClick={() => toggleSubMenu(8)} style={{ cursor: 'pointer' }}>
                        {t('System Settings')}
                        </h5>
                        {openSubMenu === 8 && (
                        <ul>
                            <li>{t('Set Permissions')}</li>
                            <li>{t('System Logs')}</li>
                        </ul>
                        )}
                        <ul>
                        <li>{t('Logout')}</li>
                        </ul>
                    </div>
                    )}
                </div>
                            </>
                        )}

         {/* Shopuser Menu */}                
        {role === 'shopuser' && (
            <>
              
      <div>
        <h4 onClick={() => toggleSubMenu(9)} style={{ cursor: 'pointer' }}>
          Shopuser Menu (店鋪用戶菜單目錄)
        </h4>
        {openSubMenu === 9 && (
          <div style={{ paddingLeft: '20px' }}>
            <h5 onClick={() => toggleSubMenu(10)} style={{ cursor: 'pointer' }}>
              Manage Shop (管理店鋪)
            </h5>
            {openSubMenu === 10 && (
              <ul>
                <li>View My Shops (查看我的店鋪)</li>
                <li>Edit Shop Information (編輯店鋪資訊)</li>
                <li>Delete Shop (刪除店鋪)</li>
              </ul>
            )}
            <h5 onClick={() => toggleSubMenu(11)} style={{ cursor: 'pointer' }}>
              Manage Products (管理產品)
            </h5>
            {openSubMenu === 11 && (
              <ul>
                <li>Create New Product (創建新產品)</li>
                <li>Edit Product (編輯產品)</li>
                <li>Delete Product (刪除產品)</li>
                <li>View Product List (查看產品列表)</li>
              </ul>
            )}
            <h5 onClick={() => toggleSubMenu(12)} style={{ cursor: 'pointer' }}>
              Subscription (訂閱)
            </h5>
            {openSubMenu === 12 && (
              <ul>
                <li>View My Subscription (查看我的訂閱)</li>
                <li>Pay Monthly Fee (支付月費)</li>
              </ul>
            )}
            <h5 onClick={() => toggleSubMenu(13)} style={{ cursor: 'pointer' }}>
              Promotions (活動)
            </h5>
            {openSubMenu === 13 && (
              <ul>
                <li>View My Promotions (查看我的活動)</li>
                <li>Create Promotion (創建活動)</li>
              </ul>
            )}
            <h5 onClick={() => toggleSubMenu(14)} style={{ cursor: 'pointer' }}>
              Coupons (禮卷)
            </h5>
            {openSubMenu === 14 && (
              <ul>
                <li>View My Coupons (查看我的禮卷)</li>
                <li>Create Coupon (創建禮卷)</li>
              </ul>
            )}
            <ul>
              <li>Logout (登出)</li>
            </ul>
          </div>
        )}
      </div>
            </>
        )}
                        {role === 'buyer' && (
                            <>
                                  {/* Buyer Menu */}
      <div>
        <h4 onClick={() => toggleSubMenu(15)} style={{ cursor: 'pointer' }}>
          Buyer Menu (買家菜單目錄)
        </h4>
        {openSubMenu === 15 && (
          <div style={{ paddingLeft: '20px' }}>
            <h5 onClick={() => toggleSubMenu(16)} style={{ cursor: 'pointer' }}>
              Dashboard (儀表板)
            </h5>
            {openSubMenu === 16 && (
              <ul>
                <li>Browse Shops (瀏覽店鋪)</li>
                <li>View by Category (按類別查看)</li>
                <li>Search Shops (搜索店鋪)</li>
              </ul>
            )}
            <h5 onClick={() => toggleSubMenu(17)} style={{ cursor: 'pointer' }}>
              Shopping Cart (購物車)
            </h5>
            {openSubMenu === 17 && (
              <ul>
                <li>View Cart (查看購物車)</li>
                <li>Checkout (結帳)</li>
              </ul>
            )}
            <h5 onClick={() => toggleSubMenu(18)} style={{ cursor: 'pointer' }}>
              My Orders (我的訂單)
            </h5>
            {openSubMenu === 18 && (
              <ul>
                <li>View Order History (查看過往訂單)</li>
                <li>Order Details (訂單詳情)</li>
              </ul>
            )}
            <h5 onClick={() => toggleSubMenu(19)} style={{ cursor: 'pointer' }}>
              Profile (個人資料)
            </h5>
            {openSubMenu === 19 && (
              <ul>
                <li>Edit Personal Information (編輯個人資訊)</li>
                <li>Change Password (修改密碼)</li>
              </ul>
            )}
            <ul>
              <li>Logout (登出)</li>
            </ul>
          </div>
        )}
      </div>
                                
                            </>
                        )}
                        {!isLoggedIn ? (
                            <Link to="/login" onClick={toggleMenu}>{t('Login')}</Link> // Show Login if not logged in
                        ) : (
                             // Show Logout if logged in
                            <>
                            <Link to="/Signup" onClick={toggleMenu}>{t('SignUp')}</Link>   
                                <Link to="/OrderPage" onClick={toggleMenu}>{t('OrderPage')}</Link>
                                <Link to="/productslist" onClick={toggleMenu}>{t('Products List')}</Link>
                                <Link to="/coursetest" onClick={toggleMenu}>{t('Course Test')}</Link>
                            <Link to="/" onClick={() => { onLogout(); toggleMenu(); }}>{t('Logout')}</Link>
                            </>
                        )}
                    </div>
                )}
            </div>
        </CartContext.Provider>
    );
};

export default Navigation;
