import React, { useState, useEffect } from 'react';
import { BrowserRouter } from 'react-router-dom';
import Navigation from './Navigation';
import Signup from './Signup';
import Login from './Login';

function App() {
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [username, setUsername] = useState(''); // 新增用户名状态
    const [role, setRole] = useState(''); // 新增角色状态

    useEffect(() => {
        const loggedIn = localStorage.getItem('isLoggedIn') === 'true';
        const storedUsername = localStorage.getItem('user.username') || ''; // 获取用户名
        const storedRole = localStorage.getItem('user.role') || ''; // 获取角色
        setIsLoggedIn(loggedIn);
        setUsername(storedUsername); // 更新用户名
        setRole(storedRole); // 更新角色
    }, []);

    const handleLoginSuccess = (user, userRole) => {
        setIsLoggedIn(true);
        setUsername(user); // 更新用户名
        setRole(userRole); // 更新角色
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('username', user); // 存储用户名
        localStorage.setItem('role', userRole); // 存储角色
    };

    const handleLogout = () => {
        setIsLoggedIn(false);
        setUsername(''); // 清空用户名
        setRole(''); // 清空角色
        localStorage.removeItem('isLoggedIn');
        localStorage.removeItem('username'); // 移除用户名
        localStorage.removeItem('role'); // 移除角色
    };

    return (
        <div className="container">
            {isLoggedIn && <Navigation onLogout={handleLogout} />}
            {isLoggedIn && <h1>Welcome, {username} ({role})!</h1>} {/* 显示欢迎消息 */}
            {!isLoggedIn && (
                <div>
                    {/* <h1>Sign Up</h1>
                    <Signup /> */}
                    <h1>Login</h1>
                    <Login onLoginSuccess={handleLoginSuccess} />
                </div>
            )}
        </div>
    );
}

export default App;
