// src/OrderPage.js
import React, { useState } from 'react';
import './OrderPage.css'; // Ensure the updated CSS file is imported
import Navigation from './Navigation';
import styles from './OrderPage.module.css';

const OrderPage = () => {
    const [quantities, setQuantities] = useState(Array(3).fill(0)); // Assuming 3 items

    const items = [
        { id: 1, name: '草莓開心果', price: 30, image: 'path_to_image_1.jpg' },
        { id: 2, name: '香橙雪糕', price: 35, image: 'path_to_image_2.jpg' },
        { id: 3, name: '覆盆子慕斯', price: 40, image: 'path_to_image_3.jpg' },
    ];

    const handleIncrease = (index) => {
        setQuantities((prev) => {
            const newQuantities = [...prev];
            newQuantities[index] += 1;
            return newQuantities;
        });
    };

    const handleDecrease = (index) => {
        setQuantities((prev) => {
            const newQuantities = [...prev];
            if (newQuantities[index] > 0) {
                newQuantities[index] -= 1;
            }
            return newQuantities;
        });
    };

    const total = quantities.reduce((sum, qty, index) => sum + qty * items[index].price, 0);

    return (
        <div className="styles.form-container">
            <Navigation />
            <h2>5月訂單</h2>
            <h2>6月團購</h2>
            <div className="styles.items">
                {items.map((item, index) => (
                    <div key={item.id} className="styles.item-card">
                        <div className="item-content">
                            <img src={item.image} alt={item.name} className="styles.item-image" />
                            <div>
                                <h3>{item.name}</h3>
                                <p>${item.price}</p>
                            </div>
                        </div>
                        <div className="styles.quantity-controls">
                            <button onClick={() => handleDecrease(index)}>-</button>
                            <span>{quantities[index]}</span>
                            <button onClick={() => handleIncrease(index)}>+</button>
                        </div>
                    </div>
                ))}
            </div>
            <div className="styles.total">
                <h3>合計: ${total}</h3>
                <button className="styles.checkout-button">下單</button>
            </div>
        </div>
    );
};

export default OrderPage;
